import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function handleKeyDown(evt, setLetter) {
  const letter = String.fromCharCode(evt.keyCode || evt.charCode);
  setLetter(letter);
  return evt;
}

const colors = [
  'red',
  'orange',
  'yellow',
  'green',
  'blue',
  'indigo',
  'violet'
]

function getColors() {
  return colors[Date.now() % colors.length]
}


function App() {
  const [currentLetter, setLetter] = useState(null);

  useEffect(() => {
    window.addEventListener("keydown", evt => handleKeyDown(evt, setLetter));
  }, []);

  return (
    <div tabIndex="0" className="App">
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div >Type a letter!</div>
        {currentLetter && <div style={{ fontSize: 400, textShadow: "10px 10px 15px" }}>{currentLetter}</div>}
      </div>
    </div>
  );
}

export default App;
