import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function handleKeyDown(evt, setLetter) {
  const letter = String.fromCharCode(evt.keyCode || evt.charCode);
  setLetter(letter);
  return evt;
}

function App() {
  const [currentLetter, setLetter] = useState(null);

  useEffect(() => {
    window.addEventListener("keydown", evt => handleKeyDown(evt, setLetter));
  }, []);

  return (
    <div tabIndex="0" className="App">
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div >Type a letter!</div>
        {currentLetter && <div style={{ fontSize: 100 }}>{currentLetter}</div>}
      </div>
    </div>
  );
}

export default App;
