import React, { useEffect, useState } from "react";
import "./App.css";

function setSpeech() {
  return new Promise(function(resolve, reject) {
    let synth = window.speechSynthesis;
    let id;

    id = setInterval(() => {
      if (synth.getVoices().length !== 0) {
        resolve(synth.getVoices());
        clearInterval(id);
      }
    }, 10);
  });
}

function handleKeyDown(evt, setLetter) {
  const letter = String.fromCharCode(evt.keyCode || evt.charCode);
  setLetter(letter);
  let utterance = new SpeechSynthesisUtterance(letter);
  return setSpeech().then(voices => {
    utterance.voice = getVoice();
    window.speechSynthesis.speak(utterance);
    return evt;
  });
}

const voices = window.speechSynthesis.getVoices();

function getVoice() {
  console.log(voices);
  return voices[Date.now() % voices.length];
}

const colors = ["red", "orange", "yellow", "green", "blue", "indigo", "violet"];

function getColor() {
  return colors[Date.now() % colors.length];
}

function App() {
  const [currentLetter, setLetter] = useState(null);

  useEffect(() => {
    window.addEventListener("keydown", evt => handleKeyDown(evt, setLetter));
  }, []);

  return (
    <div tabIndex="0" className="App">
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div>Type a letter!</div>
        {currentLetter && (
          <div
            style={{
              fontSize: 400,
              textShadow: `10px 10px 15px ${getColor()}`
            }}
          >
            {currentLetter}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
