import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function handleKeyDown(evt, setLetter) {
  console.log(String.fromCharCode(evt.keyCode || evt.charCode));
  return evt;
}

function App() {
  const [currentLetter, setLetter] = useState(null);

  useEffect(() => {
    window.addEventListener("keydown", evt => handleKeyDown(evt, setLetter));
  }, []);

  return (
    <div tabIndex="0" className="App">
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <div>Type a letter!</div>
      </div>
    </div>
  );
}

export default App;
